        pm.expect(pm.response.status).to.be.eql("OK");
    });

}

if (pm.response.code == 400){
    pm.test("Bad request.The request could not be understood by the server due to malformed syntax, modifications needed.", function () {
        pm.expect(pm.response.status).to.be.eql("Bad Request");
        pm.test('Response has the `code` field',function(){
            if(pm.expect(pm.response.json()).to.have.nested.property(`code`)){
                if(pm.expect(ld.get(pm.response.json(), `code`, null)).to.exist){
                  pm.expect(typeof ld.get(pm.response.json(), `code`, null)).to.equal("string");
                }

            }

        })

        pm.test('Response has the `message` field',function(){
            if(pm.expect(pm.response.json()).to.have.nested.property(`message`)){
                if(pm.expect(ld.get(pm.response.json(), `message`, null)).to.exist){
                  pm.expect(typeof ld.get(pm.response.json(), `message`, null)).to.equal("string");
                }

            }

        })

    });

}

